mapper.delete:
/*회원 삭제*/
DELETE
FROM
	tb_member
WHERE
	m_id = #{memberId}

/*회원 로그인 이력 삭제*/
DELETE
FROM
	tb_login
WHERE
	login_id = #{memberId}

/*구매자가 구매한 구매 이력 삭제*/
DELETE
FROM
	tb_order
WHERE
	o_id = #{memberId};

/*판매자가 등록한 상품 삭제*/
DELETE
FROM
	tb_goods
WHERE
	g_seller_id = #{memberId};

mapper.update:
/*회원정보수정*/
UPDATE tb_member

	m_pw = #{memberPw},			
	m_Level = #{memberLevel},			
	m_Name = #{memberName},			
	m_Email = #{memberEmail},			
	m_Addr = #{memberAddr}			

WHERE
	m_id = #{memberId};

mapper.select:
/*회원 아이디 중복 체크*/
SELECT
	if(COUNT(1) = 1, 0, 1)
FROM
	tb_member AS m
WHERE
	m.m_id = #{memberId};

/* 회원 등급 목록 조회 */
SELECT
	 l.level_num			AS levelNum
	 ,l.level_name			AS levelName
FROM
	tb_member_level AS l;

mapper.insert:
/* 회원가입 */
INSERT INTO tb_member
(	  m_id
	, m_pw
	, m_name
	, m_level
	, m_email
	, m_addr
	, m_reg_date
)VALUES (
	  #{memberId}
	, #{memberPw}
	, #{memberName}
	, #{memberLevel}
	, #{memberEmail}
	, #{memberAddr}
	, CURDATE()
);
